mapper.select:
SELECT safeguarding_seq ,request_department ,storage_department_code ,job_division_code ,safety_certification_bool ,autonomy_safety_check_report_bool ,safety_inspection_bool ,etc_bool ,movement_area_registration_no ,new_division_code ,safeguarding_code ,protect_gear_code ,product_manufacturer_name ,product_manufacturer_phoneno ,product_manufacturer_email ,product_name ,product_model_name ,add_description ,multiple_file_safetycertification_uuid ,multiple_file_safetyinspection_uuid ,multiple_file_autonomysafety_uuid ,protection_device_quantity ,protect_gear_quantity ,request_department_name ,request_user_id ,report_no ,approval_work_report_status_enum ,protect_gear_detail_code ,registration_dt ,expiration_dt ,manufacturing_dt ,disposal_dt ,replacement_recommend_condition ,reject_reason ,subject ,request_user_name ,insert_dtm FROM work_report_safeguarding
AND approval_work_report_status_enum IN ('APPROVAL','CLOSE') AND safeguarding_code IS NOT NULL
AND (
safety_certification_bool = true
OR autonomy_safety_check_report_bool = true
OR safety_inspection_bool = true
OR etc_bool = true
)
AND registration_dt = #{searchDto.registrationDt}
AND expiration_dt = #{searchDto.expirationDt}
AND safeguarding_code = #{searchDto.safeguardingCode}
AND product_name LIKE #{searchDto.productName}
AND product_model_name LIKE #{searchDto.productModelName}
ORDER BY safeguarding_seq DESC
